// ==========================
// MOBILE NAVIGATION TOGGLE
// ==========================
const hamburger = document.querySelector('.hamburger');
const navMenu = document.querySelector('.nav-menu');

function toggleMobileNav() {
  hamburger.classList.toggle('active');
  navMenu.classList.toggle('active');
}

hamburger.addEventListener('click', toggleMobileNav);
hamburger.addEventListener('touchstart', (e) => {
  e.preventDefault();
  toggleMobileNav();
});

document.querySelectorAll('.nav-link').forEach((n) => {
  n.addEventListener('click', () => {
    hamburger.classList.remove('active');
    navMenu.classList.remove('active');
  });
  n.addEventListener('touchend', () => {
    hamburger.classList.remove('active');
    navMenu.classList.remove('active');
  });
});

// ==========================
// SWIPE DETECTION (FOR MOBILE NAV)
// ==========================
let touchStartX = 0;
let touchEndX = 0;

document.addEventListener('touchstart', (e) => {
  touchStartX = e.changedTouches[0].screenX;
});

document.addEventListener('touchend', (e) => {
  touchEndX = e.changedTouches[0].screenX;
  handleSwipe();
});

function handleSwipe() {
  const swipeThreshold = 100;
  const swipeDistance = touchEndX - touchStartX;

  if (swipeDistance > swipeThreshold && !navMenu.classList.contains('active')) {
    toggleMobileNav(); // swipe right → open
  } else if (swipeDistance < -swipeThreshold && navMenu.classList.contains('active')) {
    toggleMobileNav(); // swipe left → close
  }
}

// ==========================
// SMOOTH SCROLLING FOR ANCHORS
// ==========================
document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
  anchor.addEventListener('click', function (e) {
    e.preventDefault();
    const target = document.querySelector(this.getAttribute('href'));
    if (target) {
      target.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  });
});

// ==========================
// NAVBAR SCROLL EFFECT
// ==========================
let ticking = false;

function updateNavbar() {
  const navbar = document.querySelector('.navbar');
  if (window.scrollY > 100) {
    navbar.style.background = 'rgba(42, 45, 53, 0.98)';
    navbar.style.boxShadow = '0 2px 20px rgba(0, 0, 0, 0.3)';
  } else {
    navbar.style.background = 'rgba(42, 45, 53, 0.95)';
    navbar.style.boxShadow = '0 2px 10px rgba(0, 0, 0, 0.2)';
  }
  ticking = false;
}

window.addEventListener('scroll', () => {
  if (!ticking) {
    requestAnimationFrame(updateNavbar);
    ticking = true;
  }
});

// ==========================
// SCROLL ANIMATION (Intersection Observer)
// ==========================
const observerOptions = {
  threshold: 0.1,
  rootMargin: '0px 0px -50px 0px',
};

const observer = new IntersectionObserver((entries) => {
  entries.forEach((entry) => {
    if (entry.isIntersecting) {
      entry.target.classList.add('animate');
    }
  });
}, observerOptions);

document
  .querySelectorAll(
    '.scroll-animate-left, .scroll-animate-right, .scroll-animate-scale, .scroll-animate-stagger, .scroll-animate-rotate'
  )
  .forEach((el) => observer.observe(el));

// ==========================
// CUSTOM CURSOR ANIMATION
// ==========================
const coords = { x: 0, y: 0 };
const circles = document.querySelectorAll('.cursor-follower, .cursor-dot');

circles.forEach((circle) => {
  circle.x = 0;
  circle.y = 0;
});

function animateCircles() {
  let x = coords.x;
  let y = coords.y;

  circles.forEach((circle, index) => {
    circle.style.left = `${x - 12}px`;
    circle.style.top = `${y - 12}px`;
    circle.style.transform = `scale(${(circles.length - index) / circles.length})`;

    circle.x = x;
    circle.y = y;

    const nextCircle = circles[index + 1] || circles[0];
    x += (nextCircle.x - x) * 0.3;
    y += (nextCircle.y - y) * 0.3;
  });

  requestAnimationFrame(animateCircles);
}

animateCircles();

window.addEventListener('mousemove', (e) => {
  coords.x = e.clientX;
  coords.y = e.clientY;
});

document.addEventListener('mouseenter', () => {
  circles.forEach((circle) => (circle.style.opacity = '1'));
});

document.addEventListener('mouseleave', () => {
  circles.forEach((circle) => (circle.style.opacity = '0'));
});

// ==========================
// CONTACT FORM (EmailJS Integration)
// ==========================
(function () {
  emailjs.init('YOUR_PUBLIC_KEY');
})();

document.getElementById('contactForm').addEventListener('submit', function (event) {
  event.preventDefault();

  const form = event.target;
  const submitBtn = form.querySelector('button[type="submit"]');
  const originalText = submitBtn.textContent;

  submitBtn.textContent = 'Sending...';
  submitBtn.disabled = true;

  emailjs
    .sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form)
    .then(() => {
      alert('Message sent successfully!');
      form.reset();
    })
    .catch((error) => {
      console.error('Failed to send message:', error);
      alert('Failed to send message. Please try again.');
    })
    .finally(() => {
      submitBtn.textContent = originalText;
      submitBtn.disabled = false;
    });
});
